import './normalize.css';
import './App.css';

function App() {
  const logoSize = 100;

  return (
    <div className="App">
      <div className="main-container">
        {/* Другие компоненты и элементы */}

        {/* Новый контейнер для логотипа и информации "About" */}
        <div className="logo-and-about">
          <div className="logo-overlay">
            <img
              className="myLogo"
              src="https://static7.vivoo.ru/datas/photos/80x80/18/ed/ef85103be2a896f1960a96fbadd1.jpg?0"
              height={logoSize}
              width={logoSize}
              alt="my logo"
            />
          </div>
          <div className="about-container">
            <About
              category_name="Категория"
              descriptions={["Первое описание", "Второе описание", "Третье описание"]}
            />
            <About
              category_name="Категория"
              descriptions={["Первое описание", "Второе описание", "Третье описание"]}
            />
          </div>
        </div>

        {/* Остальные компоненты */}
        <div className="contact_n_short_info">
          <div className="contacts_container"></div>
        </div>
      </div>
    </div>
  );
}

function WorkExperience({
  title = 'work name',
  duration = 'work duration',
  description = 'work description',
  company = 'enterprise name',
}) {
  return (
    <div className="work">
      <h3 className="work-name">{title}</h3>
      <p className="work-duration">
        {duration} - {company}
      </p>
      <p className="enterprise-name"></p>
      <p className="work-description">{description}</p>
    </div>
  );
}

function About({ category_name, descriptions }) {
  return (
    <div className="about">
      <h2 className="category-name">{category_name}</h2>
      {descriptions.map((item, index) => (
        <p key={index} className="about-description">
          {item}
        </p>
      ))}
    </div>
  );
}

export default App;
