import './normalize.css';
import './App.css';
import React, { useState,  useId } from 'react';
function App() {
  const logoSize = 100;

  return (
    <div className="App">
      <div className='left'>
        <img
          className="logo"
          src="logo.png"
          alt="logo"></img>
        <div className='about__container'>
          <div className='about'>
            <h2>Contacts</h2>
            <div className='about__info'>
              <div className='c__container'> 
                <p>C:</p>
                <p>E:</p>
              </div>
              <div className='info__container'>
                <p>+7 777 777 77 77</p>
                <p>chtnylav@gmail.com</p>
              </div>
              <React.Fragment/>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

function WorkExperience({
  title = 'work name',
  duration = 'work duration',
  description = 'work description',
  company = 'enterprise name',
}) {
  return (
    <div className="work">
      <h3 className="work__name">{title}</h3>
      <p className="work__duration">
        {duration} - {company}
      </p>
      <p className="work__description">{description}</p>
    </div>
  );
}

function About({ category_name = "", descriptions }) {
  return (
    <div className="about">
      {category_name && <h2 className="about__category-name">{category_name}</h2>}
      {descriptions.map((item, index) => {
        if (typeof item === "string") {
          return (
            <p key={index} className="about__container-description">
              {item}
            </p>
          );
        } else {
          return (
              <CopyTextComponent key={index} text={item} />
            )

          }


      })}
    </div>
  );
}
const CopyTextComponent = ({ text }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = () => {
    if (typeof text !== 'string') {
      console.error('Ошибка: text должен быть строкой.');
      return;
    }
    navigator.clipboard.writeText(text)
      .then(() => {
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      })
      .catch((err) => {
        console.error('Ошибка при копировании текста: ', err);
      });
  };
  return (
    <div className={'copy-container'+useId()}>
      {copied && <div className="copy-message">Текст скопирован!</div>}
      <p onClick={handleCopy} className="copy-text">
        {text}
      </p><React.Fragment/>
    </div>
  );
};

export default App;
