import './normalize.css';
import './App.css';
import React, { useState } from 'react';
function App() {
  const logoSize = 100;

  return (
    <div className="App">
      <div className="App__main">
        <div className="main__initials-container">
          <div className="main__initials">
            <h1 className="initials__name">Your Name</h1>
            <h2 className="initials__profession">Senior Software Engineer</h2>
            <div className="initials__experience">
              <h3 className="initials__section-header">EXPERIENCE</h3>
              <div className="initials__list">
                <WorkExperience
                  title="Frontend Developer"
                  duration="3 месяца"
                  description="Разработка фронта для интернет-магазина, рефакторинг legacy-кода, настройка Webpack"
                  company="Shop24"
                />
                <WorkExperience
                  title="Fullstack Developer"
                  duration="1 год 3 месяца"
                  description="Разработка полного стека для интернет-магазина, создание REST API, рефакторинг legacy-кода, настройка Webpack"
                  company="Shop24"
                />
                <WorkExperience
                  title="Senior Fullstack Developer"
                  duration="2 года 5 месяцев"
                  description="Разработка полного стека для интернет-магазина, создание REST API, рефакторинг legacy-кода, настройка Webpack"
                  company="Shop24"
                />
                <WorkExperience
