function About({ category_name = "", descriptions }) {
  return (
    <div className="about">
      {category_name && <h2 className="about__category-name">{category_name}</h2>}
      {descriptions.map((item, index) => {
        if (typeof item === "string") {
          return (
            <p key={index} className="about__description">
              {item}
            </p>
          );
        } else {
        return (
            <CopyTextComponent key={index} text={item.text} />)

          }


      })}
    </div>
  );
}

const CopyTextComponent = ({ text }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = () => {
    navigator.clipboard.writeText(text).then(() => {
      setCopied(true);
      setTimeout(() => setCopied(false), 2000); // Сообщение пропадет через 2 секунды
    });
  };

  return (
    <div type="copy">
      <div className="copy-container">
        {copied && <div className="copy-message">Текст скопирован!</div>}
        <p  onClick={handleCopy} className="copy-text">
          {text}
        </p>
      </div>
    </div>
  );
};
